package JAVA_Basic;

/*
	八种数据类型
 */
public class java009八种数据类型 {

    public static void main(String[] args) {
        // TODO Auto-generated method stub
        byte b = 1;// 1字节
        short s = 10;//2字节
        int i = 10;// 4字节
        long lon = 1001;// 8字节
        float f = 10.99f;// 4字节 后缀f
        double d = 10.1234560;// 8字节

        char c = 'A';// 2字节
        boolean bool = true;// 1字节

        System.out.println(c);
    }
}
